import java.util.*;
class Solution {
    public int[] solution(String s) {
        int[] answer = new int[2];
        int countZero = 0;
        int count = 0;
        while(!s.equals("1")) {
            int n = 0;
            for (int i=0; i<s.length(); i++) {
                if (s.charAt(i) == '1') {
                    n++;
                }
            }
            countZero += s.length() - n;
            
            String binary = "";
            while(n != 0) {
                binary += n % 2;
                n /= 2;
            }
            s = "";
            for (int i=binary.length() - 1; i>=0; i--) {
                s += binary.charAt(i);
            }
            count++;
        }
        answer[0] = count;
        answer[1] = countZero;
        return answer;
    }
}
```

