import java.util.*;
class Solution {
    public int solution(int n, int[] lost, int[] reserve) {
        Arrays.sort(lost); Arrays.sort(reserve);
        List<Integer> rList = new ArrayList<>();
        List<Integer> lList = new ArrayList<>();
        for (int r : reserve) {
            rList.add(r);
