        for (int l : lost) {
            lList.add(l);
        }
        for (int l : lost) {
            if(rList.contains(l)) {
                rList.remove(Integer.valueOf(l));
                lList.remove(Integer.valueOf(l));
            }
        }
        int count = lList.size();
        for (Integer l : lList) {
            if(rList.contains(l - 1)) {
                count--;
                rList.remove(Integer.valueOf(l - 1));
            }
            else if(rList.contains(l + 1)) {
                count--;
                rList.remove(Integer.valueOf(l + 1));
            }
        }
        return n - count;
    }
}
