import java.util.*;
class Solution {
    public int[] solution(int[] answers) {
        ArrayList<int[]> nums = new ArrayList<>();
        nums.add(new int[] {1, 2, 3, 4, 5});
        nums.add(new int[] {2, 1, 2, 3, 2, 4, 2, 5});
