import java.util.*;
class Solution {
    public int solution(int[] scoville, int K) {
        int answer = 0;
        boolean flag = false;
        PriorityQueue<Integer> pq = new PriorityQueue<>();
        for (int s : scoville) {
            pq.add(s);
            if (s != 0) flag = true;
        }
        if (!flag) return -1;
        while (pq.peek() < K) {
            if (pq.size() < 2) return -1;
            int a = pq.poll();
            int b = pq.poll();
            pq.add(a + b * 2);
            answer++;
        }
        return answer;
    }
}
```

