class Solution {
    public static boolean[] isVisit;
    public int solution(int n, int[][] computers) {
        int answer = 0;
        isVisit = new boolean[n];
        
        for (int i=0; i<n; i++) {
            if (!isVisit[i]) {
                isVisit[i] = true;
                answer++;
                dfs(i, n, computers);
            }
        }
        return answer;
    }
    public void dfs(int j, int n, int[][] coms) {
        for (int k=0; k<n; k++) {
            if (!isVisit[k] && coms[j][k] == 1) {
                isVisit[k] = true;
                dfs(k, n, coms);
            }
        }
    }
}
