    static int N, M, days;
    static int[][] tomatoes;
    static Queue<Tomato> queue;
    static int[] dirX = { -1, 1, 0, 0 };
    static int[] dirY = { 0, 0, -1, 1 };

    public static class Tomato {
        private int x;
        private int y;
        private int day;

        public Tomato(int x, int y, int day) {
            this.x = x;
            this.y = y;
            this.day = day;
        }

        public int getDay() {
            return day;
        }

        public void setDay(int day) {
            this.day = day;
        }

        public int getX() {
            return x;
        }

        public void setX(int x) {
            this.x = x;
        }

        public int getY() {
            return y;
        }

        public void setY(int y) {
            this.y = y;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        // ì…ë ¥
        M = Integer.parseInt(st.nextToken());
        N = Integer.parseInt(st.nextToken());
        tomatoes = new int[N][M];
        queue = new LinkedList<>();
        days = 0;
        for(int i=0; i<N; i++) {
            st = new StringTokenizer(br.readLine());
            for(int j=0; j<M; j++) {
                tomatoes[i][j] = Integer.parseInt(st.nextToken());
            }
        }

        for(int i=0; i<N; i++) {
            for(int j=0; j<M; j++) {
                if(tomatoes[i][j] == 1) {
                    queue.add(new Tomato(j, i, 0));
                }
            }
        }

        while(!queue.isEmpty()) {
            days = queue.peek().getDay();
            bfs(queue.poll());
        }

        for(int i=0; i<N; i++) {
            for(int j=0; j<M; j++) {
                if(tomatoes[i][j] == 0) {
                    days = -1;
                    break;
                }
            }
        }

        System.out.println(days);
        br.close();
    }

    private static void bfs(Tomato tomato) {
        tomatoes[tomato.getY()][tomato.getX()] = 1;
        int nextLevel = tomato.getDay() + 1;
        for(int i=0; i<4; i++) {
            int nextX = tomato.getX() + dirX[i];
            int nextY = tomato.getY() + dirY[i];
            if(isPossible(nextX, nextY) && tomatoes[nextY][nextX] == 0) {
                tomatoes[nextY][nextX] = 1;
                queue.add(new Tomato(nextX, nextY, nextLevel));
            }
        }
    }

    private static boolean isPossible(int x, int y) {
        if(x >= 0 && x < M && y >= 0 && y < N) return true;
        return false;
    }
}
```

